import React, { Component } from "react";
import Form from "../components/Form";
import ViewBtn from "../components/ViewBtn";
import SaveBtn from "../components/SaveBtn";

import { List, ListItem } from "../components/List";
import API from "../utils/API";

class Search extends Component {
  state = {
    search: "",
    books: []
  };

  // When this component mounts, search the Giphy API for pictures of kittens
  componentDidMount() {
    this.searchBooks();
  }

  searchBooks = query => {
    API.searchBooks(query)
      .then(res => this.setState({ books: res.data }))
      .catch(err => console.log(err));
  };


  // When the form is submitted, search the Giphy API for `this.state.search`
  handleFormSubmit = event => {
    event.preventDefault();
    this.searchBooks(this.state.search);
  };

  render() {
    return (
      <div>
        <Form
          search={this.state.search}
          handleFormSubmit={this.handleFormSubmit}
        />

              <List>
                {/* {this.state.books.map(book => ( */}
                  <ListItem 
                    // key={this.state.books.items.id}
                    >
         <img alt={this.state.books.items.volumeInfo.title} className="img-fluid"
          src={this.state.books.items.imageLinks.smallThumbnail}
            />
          <a href={this.state.books.items.previewLink}>Preview Link</a>
          <ViewBtn/><SaveBtn/>
                  </ListItem>
                {/* ))} */}
              </List>

      </div>
    );
  }
}

export default Search;
