import React, { Component } from "react";
import { Form, FormBtn } from "../components/Form";
import ViewBtn from "../components/ViewBtn";
import SaveBtn from "../components/SaveBtn";

import { List, ListItem } from "../components/List";
import API from "../utils/API";

class Search extends Component {
    state = {
        // search: "",
        books: [],
        text: "Save"

    };

    loadBooks = () => {
        if (this.state.search) {
            API.searchBooks(this.state.search)
                .then(res => this.setState({ books: res.data.items }))
                .catch(err => console.log(err))
        }
        else {
            alert("Enter name of a book to begin search");
        }




    };

    create = (id, title, image, link) => {

        API.saveBook({
            id: id,
            title: title,
            image: image,
            link: link
        })
            .then(res => { this.loadBooks(); console.log(res.data) })
            .catch(err => console.log(err));


    }

    handleInputChange = event => {
        const { search, value } = event.target;
        this.setState({
            search: value
        });
    };


    // When the form is submitted, search the Giphy API for `this.state.search`
    handleFormSubmit = event => {
        event.preventDefault();
        this.loadBooks(this.state.search);
    };

    render() {
        return (
            <div>
                <form>
                    <Form
                        // name="bookSearch"
                        value={this.state.search}
                        onChange={this.handleInputChange}
                        placeholder="Search For a Book"

                    />

                    <FormBtn
                        onClick={this.handleFormSubmit}
                    >
                        Search Book
        </FormBtn>
                </form>
                <div>
                    {this.state.books.length > 0 ? (
                        <List>

                            {this.state.books.map(book => (
                                <ListItem key={book.id}      >
                                <div className="row">            
                                <div className="col-12">                   
                                   <div className="card">
                                   <strong>{book.volumeInfo.title}</strong>

                                    <img className="card-img-top" src={book.volumeInfo.imageLinks === undefined
                                            ? "No image to show"
                                            : `${book.volumeInfo.imageLinks.thumbnail}`}
                                            alt={book.volumeInfo.title}/>
                                    <div className="card-body">                                         {/* </CardTitle> */}

                                        {/* <CardImg top width="100%" src={book.volumeInfo.imageLinks === undefined
                                            ? "No image to show"
                                            : `${book.volumeInfo.imageLinks.thumbnail}`}
                                            alt={book.volumeInfo.title} />
                                        <CardBody> */}
                                            <ViewBtn href={book.volumeInfo.previewLink} />
                                            <SaveBtn
                                                onClick={
                                                    () => this.create(book.id, book.volumeInfo.title, book.volumeInfo.imageLinks.thumbnail, book.volumeInfo.previewLink)}
                                            >  Save
                                </SaveBtn>
                                        {/* </CardBody>
                                    </Card> */}

                                    {/* <img alt={book.volumeInfo.title} src={book.volumeInfo.imageLinks === undefined
                                        ? "No image to show"
                                        : `${book.volumeInfo.imageLinks.thumbnail}`}></img>

                                    {/* <a href={book.volumeInfo.previewLink}>
                                    Preview Link                    </a>*/} 
                                    </div>
                                    </div>
                                    </div>
                                    </div> 

                                </ListItem>
                            ))}

                        </List>

                    ) : (
                            <h3>Search for a book</h3>
                        )}
                </div>
            </div>
        );
    }
}

export default Search;
